import { IconButton } from '@mui/material';
import './content.css';
import SendIcon from '@mui/icons-material/Send';

export function Contact() {
  const sendMessage = () => {
    document.getElementById("sendBtn").classList.add("slide-20");
    // document.getElementById("sendBtn").style.color = "blue"
  }

  return (
    <div style={{color: "black"}} id="contact" className="flex align-center justify-center width-100 height-100 column">
      <textarea style={{width: "25vw", height: "15vw"}} className="box-shadow no-border padding-10px margin-20px"></textarea>
      <IconButton color="primary" aria-label="send">
        <SendIcon className="align-self-start" id="sendBtn" onClick={sendMessage}/>
      </IconButton>
    </div>
  )
}
