/*** Global Constants ***/

/*** Interfaces ***/
interface CardBucket {
    /*** Required Representation ***/

    readonly cards: Array<Card>;
    readonly size: {width: number, height: number};

    /**
     * Drops a card into the bucket such that the droped card is either on the floor
     * of the bucket or is adjacent but not overlapping another card
     * 
     * @param card to drop into the bucket, must be smaller in size than the bucket
     */
    drop(card: Card): void;
}

/*** Classes ***/
class Rect {
    /*** Representation ***/

    /** 
     * AF(width, height, loc) =
     * 
     * Representation Invariant
     * 
     * Representation Exposure
     * 
     */

    constructor(public readonly x1: number, public readonly y1: number, public readonly x2: number, public readonly y2: number) {}

    public overlaps(a: number, b: number): boolean {
        if (a <= this.x1 && this.x1 <= b) return true;
        if (a <= this.x2 && this.x2 <= b) return true;
        return false;
    }
}
export class Card {
    /*** Representation ***/
    public angle = 0;
    public rect: Rect;
    public margin = 0;
    public center: number;

    /** 
     * AF(width, height, loc) =
     * 
     * Representation Invariant
     * 
     * Representation Exposure
     * 
     */

    constructor(readonly width: number, readonly height: number) {
        this.rect = new Rect(0, 0, width, height);
        this.center = width / 2;
    }

    /**
     * 
     * @param a 
     * @param b 
     */
    public peak(a: number, b: number): {x: number, y: number} {
        console.log(`Finding the peak of (${this.rect.x1}, ${this.rect.x2}) on interval (a, b)`)

        const x2 = this.width * Math.cos(this.angle);
        const y2 = this.width * Math.sin(this.angle);
        const f = (x: number) => (this.rect.y1 - y2) / (this.rect.x1 - x2) * (x - x2) + y2;

        if (this.rect.x1 < a && b < x2) return f(a) > f(b) ? {x: a, y: f(a)} : {x: b, y: f(b)};
        if (this.rect.x1 < a && b > x2) return f(a) > f(x2) ? {x: a, y: f(a)} : {x: x2, y: f(x2)};
        if (this.rect.x1 > a && b > x2) return f(this.rect.x1) > f(x2) ? {x: this.rect.x1, y: f(this.rect.x1)} : {x: x2, y: f(x2)};
        if (this.rect.x1 >= a && b <= x2) return f(this.rect.x1) > f(b) ? {x: this.rect.x1, y: f(this.rect.x1)} : {x: b, y: f(b)};
        else throw new Error("Specification violated");

    }
}

export class CardBucket2D implements CardBucket {
    /*** Representation ***/
    readonly cards: Card[] = [];

    /** 
     * AF(cards) =
     * 
     * Representation Invariant
     * 
     * Representation Exposure
     * 
     */

    constructor(readonly size: {width: number, height: number}) {}

    /** @inheritdoc */
    public drop(card: Card): void {
        const w = card.width;
        const center = Math.random() * (this.size.width - w + 1) + w / 2;

        const leftPeak = this._peak(center - w / 2, center);
        const rightPeak = this._peak(center, center + w / 2);

        card.center = center;
        card.margin = (leftPeak.y + rightPeak.y) / 2;
        if (card.margin !== 0) card.angle = Math.atan((leftPeak.y - rightPeak.y) / (leftPeak.x - rightPeak.x));

        this.cards.push(card);
    }

    /*** Helper Methods ***/
    /**
     * Finds the card with the highest peak within the interval [a, b]
     * 
     * @param a the start of the interval, must be 0 <= a <= this.width
     * @param b the end of the interval, must be 0 <= a < b <= this.width
     * @returns the coordinates of the peak within the interval and has the heighest peak
     */
    private _peak(a: number, b: number): {x: number, y: number} {
        let peak = {x: 0, y: 0};
        for (const card of this.cards) {
            if (!card.rect.overlaps(a, b)) continue;
            console.log("Found an overlap!")

            const cardPeak = card.peak(a, b);
            if (cardPeak.y > peak.y || (cardPeak.y === peak.y && cardPeak.x > peak.x)) peak = cardPeak;
        }

        return peak;
    }
}
